mapper.select:
SELECT od.order_detail_id,od.order_id,od.dish_id,od.quantity,od.create_by,od.create_time,od.update_by,od.update_time
FROM sale_order_detail od
WHERE od.order_detail_id = #{orderDetailId,jdbcType=BIGINT}

SELECT od.order_detail_id,od.order_id,od.dish_id,od.quantity,od.create_by,od.create_time,od.update_by,od.update_time FROM sale_order_detail od
and od.order_detail_id = #{orderDetailId,jdbcType=BIGINT}
ORDER BY od.order_detail_id ASC
